import React, { useMemo, useState, useEffect, useRef } from 'react';
import { GoogleGenAI } from "@google/genai";
import { MOCK_USERS } from '../data';
import { Order, OrderStatus, MenuItem, MealCategory, User } from '../types';
import { useAppContext, OrderStatusBadge, StarRatingDisplay } from '../App';
import { ChevronDownIcon, ClockIcon, MenuIcon, PlusIcon, TrashIcon, BuildingOfficeIcon, SearchIcon, ShieldCheckIcon, CashIcon, StarIcon, ClipboardDocumentListIcon, SparklesIcon, SpinnerIcon, BellIcon, XMarkIcon } from './common/Icons';

type View = 'orders' | 'menu' | 'reviews';

const AddMenuItemModal = ({ onClose, onAdd }: { onClose: () => void; onAdd: (item: Omit<MenuItem, 'id' | 'vendorId'>) => void; }) => {
    const [name, setName] = useState('');
    const [category, setCategory] = useState<MealCategory>(MealCategory.Lunch);
    const [price, setPrice] = useState('');
    const [imageUrl, setImageUrl] = useState('');
    const [description, setDescription] = useState('');
    const [isGenerating, setIsGenerating] = useState(false);
    const [errors, setErrors] = useState<{ [key: string]: string }>({});

    const validate = () => {
        const newErrors: { [key: string]: string } = {};
        if (!name.trim()) newErrors.name = 'Name is required.';
        if (!price || isNaN(Number(price)) || Number(price) <= 0) newErrors.price = 'Price must be a positive number.';
        if (!imageUrl.trim()) {
            newErrors.imageUrl = 'Image URL is required.';
        } else {
            try {
                new URL(imageUrl);
            } catch (_) {
                newErrors.imageUrl = 'Please enter a valid URL.';
            }
        }
        setErrors(newErrors);
        return Object.keys(newErrors).length === 0;
    };

    const handleGenerateDescription = async () => {
        if (!name.trim()) {
            setErrors(prev => ({ ...prev, name: 'Please enter an item name first.'}));
            return;
        }
        setIsGenerating(true);
        try {
            const ai = new GoogleGenAI({ apiKey: process.env.API_KEY as string });
            const response = await ai.models.generateContent({
                model: "gemini-2.5-flash",
                contents: `Write a short, delicious, and enticing description for a menu item named '${name}' for a corporate food app. The description should be about 15-20 words.`
            });
            setDescription(response.text.trim().replace(/"/g, ''));
        } catch (error) {
            console.error("Failed to generate description:", error);
        } finally {
            setIsGenerating(false);
        }
    };

    const handleSubmit = () => {
        if (validate()) {
            onAdd({
                name,
                category,
                price: Number(price),
                imageUrl,
                description,
            });
            onClose();
        }
    };
    
    return (
        <div className="fixed inset-0 bg-black bg-opacity-60 dark:bg-opacity-80 flex justify-center items-center z-50 p-4">
            <div className="bg-white dark:bg-slate-900 rounded-xl shadow-2xl w-full max-w-lg">
                <div className="p-6 border-b border-slate-200 dark:border-slate-700">
                    <h3 className="text-xl font-bold text-slate-800 dark:text-slate-100">Add New Menu Item</h3>
                </div>
                <div className="p-6 space-y-5">
                    <div>
                        <label htmlFor="name" className="block text-sm font-medium text-slate-700 dark:text-slate-300 mb-1">Item Name</label>
                        <input type="text" id="name" value={name} onChange={e => setName(e.target.value)} className={`w-full border rounded-lg px-3 py-2 focus:outline-none focus:ring-2 focus:ring-indigo-500 bg-white dark:bg-slate-800 dark:text-slate-200 ${errors.name ? 'border-red-500' : 'border-slate-300 dark:border-slate-600'}`} />
                        {errors.name && <p className="text-red-500 text-xs mt-1">{errors.name}</p>}
                    </div>
                    <div>
                        <label htmlFor="description" className="block text-sm font-medium text-slate-700 dark:text-slate-300 mb-1">Description</label>
                        <textarea id="description" value={description} onChange={e => setDescription(e.target.value)} rows={3} className="w-full border border-slate-300 dark:border-slate-600 rounded-lg px-3 py-2 focus:outline-none focus:ring-2 focus:ring-indigo-500 bg-white dark:bg-slate-800 dark:text-slate-200" placeholder="A delicious summary of the item..."></textarea>
                        <button onClick={handleGenerateDescription} disabled={isGenerating} className="mt-2 flex items-center gap-2 text-sm font-semibold text-indigo-600 dark:text-indigo-400 hover:text-indigo-800 dark:hover:text-indigo-300 disabled:opacity-50">
                            {isGenerating ? <SpinnerIcon className="w-4 h-4 animate-spin"/> : <SparklesIcon className="w-4 h-4" />}
                            {isGenerating ? 'Generating...' : 'Generate with AI'}
                        </button>
                    </div>
                    <div>
                        <label htmlFor="category" className="block text-sm font-medium text-slate-700 dark:text-slate-300 mb-1">Category</label>
                        <select id="category" value={category} onChange={e => setCategory(e.target.value as MealCategory)} className="w-full border border-slate-300 dark:border-slate-600 rounded-lg px-3 py-2 focus:outline-none focus:ring-2 focus:ring-indigo-500 bg-white dark:bg-slate-800 dark:text-slate-200">
                            {Object.values(MealCategory).map(cat => <option key={cat} value={cat}>{cat}</option>)}
                        </select>
                    </div>
                     <div>
                        <label htmlFor="price" className="block text-sm font-medium text-slate-700 dark:text-slate-300 mb-1">Price</label>
                        <input type="number" id="price" value={price} onChange={e => setPrice(e.target.value)} placeholder="150" min="0" step="1" className={`w-full border rounded-lg px-3 py-2 focus:outline-none focus:ring-2 focus:ring-indigo-500 bg-white dark:bg-slate-800 dark:text-slate-200 ${errors.price ? 'border-red-500' : 'border-slate-300 dark:border-slate-600'}`} />
                        {errors.price && <p className="text-red-500 text-xs mt-1">{errors.price}</p>}
                    </div>
                     <div>
                        <label htmlFor="imageUrl" className="block text-sm font-medium text-slate-700 dark:text-slate-300 mb-1">Image URL</label>
                        <input type="text" id="imageUrl" value={imageUrl} onChange={e => setImageUrl(e.target.value)} placeholder="https://..." className={`w-full border rounded-lg px-3 py-2 focus:outline-none focus:ring-2 focus:ring-indigo-500 bg-white dark:bg-slate-800 dark:text-slate-200 ${errors.imageUrl ? 'border-red-500' : 'border-slate-300 dark:border-slate-600'}`} />
                        {errors.imageUrl && <p className="text-red-500 text-xs mt-1">{errors.imageUrl}</p>}
                    </div>
                </div>
                <div className="bg-slate-50 dark:bg-slate-800/50 px-6 py-4 flex justify-end space-x-3 rounded-b-xl">
                    <button onClick={onClose} className="px-5 py-2.5 bg-slate-200 text-slate-700 dark:bg-slate-700 dark:text-slate-200 rounded-lg font-semibold hover:bg-slate-300 dark:hover:bg-slate-600 transition-colors">Cancel</button>
                    <button onClick={handleSubmit} className="px-5 py-2.5 bg-indigo-600 text-white rounded-lg font-semibold hover:bg-indigo-700 transition-colors">Add Item</button>
                </div>
            </div>
        </div>
    );
};

const DeleteConfirmationModal = ({ item, onClose, onConfirm }: { item: MenuItem; onClose: () => void; onConfirm: () => void; }) => (
    <div className="fixed inset-0 bg-black bg-opacity-60 dark:bg-opacity-80 flex justify-center items-center z-50 p-4">
        <div className="bg-white dark:bg-slate-900 rounded-xl shadow-2xl w-full max-w-md">
            <div className="p-8">
                <h3 className="text-xl font-bold text-slate-800 dark:text-slate-100">Confirm Deletion</h3>
                <p className="text-slate-600 dark:text-slate-300 mt-2">
                    Are you sure you want to delete <span className="font-semibold text-slate-900 dark:text-white">"{item.name}"</span>?
                    This action cannot be undone.
                </p>
            </div>
            <div className="bg-slate-50 dark:bg-slate-800/50 px-6 py-4 flex justify-end space-x-3 rounded-b-xl">
                <button onClick={onClose} className="px-5 py-2.5 bg-slate-200 text-slate-700 dark:bg-slate-700 dark:text-slate-200 rounded-lg font-semibold hover:bg-slate-300 dark:hover:bg-slate-600 transition-colors">Cancel</button>
                <button onClick={onConfirm} className="px-5 py-2.5 bg-red-600 text-white rounded-lg font-semibold hover:bg-red-700 transition-colors">Delete</button>
            </div>
        </div>
    </div>
);

const ManageMenuView = ({ menuItems, onAddNew, onDelete }: { menuItems: MenuItem[]; onAddNew: () => void; onDelete: (item: MenuItem) => void; }) => {
  const [searchQuery, setSearchQuery] = useState('');

  const filteredMenuItems = useMemo(() => {
    return menuItems
      .filter(item => item.name.toLowerCase().includes(searchQuery.toLowerCase()))
      .sort((a,b) => a.name.localeCompare(b.name));
  }, [menuItems, searchQuery]);

  return (
    <div>
        <div className="flex flex-col md:flex-row justify-between items-center mb-6 gap-4">
            <div className="relative flex-grow w-full md:max-w-md">
                <div className="absolute inset-y-0 left-0 pl-4 flex items-center pointer-events-none">
                    <SearchIcon className="h-5 w-5 text-slate-400" />
                </div>
                <input
                    type="text"
                    placeholder="Search menu items..."
                    value={searchQuery}
                    onChange={(e) => setSearchQuery(e.target.value)}
                    className="w-full block pl-11 pr-4 py-2.5 border border-slate-300 dark:border-slate-600 rounded-full focus:outline-none focus:ring-2 focus:ring-indigo-500 bg-white dark:bg-slate-800 dark:text-slate-200"
                />
            </div>
            <button onClick={onAddNew} className="w-full md:w-auto flex items-center justify-center gap-2 px-5 py-2.5 bg-indigo-600 text-white rounded-lg font-semibold hover:bg-indigo-700 transition-colors flex-shrink-0">
                <PlusIcon className="w-5 h-5" />
                <span>Add New Item</span>
            </button>
        </div>
        <div className="bg-white dark:bg-slate-800 rounded-xl shadow-lg overflow-x-auto">
        <table className="w-full text-sm text-left text-slate-500 dark:text-slate-400">
            <thead className="text-xs text-slate-700 dark:text-slate-300 uppercase bg-slate-50 dark:bg-slate-700/50">
            <tr>
                <th scope="col" className="px-6 py-4 font-semibold">Item</th>
                <th scope="col" className="px-6 py-4 font-semibold">Category</th>
                <th scope="col" className="px-6 py-4 font-semibold">Price</th>
                <th scope="col" className="px-6 py-4 font-semibold text-right">Actions</th>
            </tr>
            </thead>
            <tbody>
            {filteredMenuItems.map(item => (
                <tr key={item.id} className="bg-white dark:bg-slate-800 border-b dark:border-slate-700 hover:bg-slate-50/50 dark:hover:bg-slate-700/50">
                <td className="px-6 py-4 font-medium text-slate-900 dark:text-white">
                    <div className="flex items-center gap-4">
                    <img src={item.imageUrl} alt={item.name} className="w-16 h-16 object-cover rounded-lg"/>
                    <span className="font-bold text-base">{item.name}</span>
                    </div>
                </td>
                <td className="px-6 py-4">{item.category}</td>
                <td className="px-6 py-4 font-semibold text-slate-700 dark:text-slate-200">â‚¹{item.price}</td>
                <td className="px-6 py-4 text-right">
                    <button onClick={() => onDelete(item)} className="p-2 text-slate-500 dark:text-slate-400 hover:text-red-600 hover:bg-red-100 dark:hover:bg-red-500/20 dark:hover:text-red-400 rounded-full transition-colors" aria-label={`Delete ${item.name}`}>
                        <TrashIcon className="w-5 h-5" />
                    </button>
                </td>
                </tr>
            ))}
            </tbody>
        </table>
         {filteredMenuItems.length === 0 && (
            <div className="text-center py-16 text-slate-500 dark:text-slate-400">
                <p className="font-semibold text-lg text-slate-700 dark:text-slate-200">No menu items found</p>
                <p className="text-sm mt-1">Try adjusting your search or add a new item.</p>
            </div>
         )}
        </div>
    </div>
  );
};

const ReviewsView = ({ orders, menuItems, users }: { orders: Order[], menuItems: MenuItem[], users: User[] }) => {
    const reviews = useMemo(() => {
        return orders
            .filter(o => typeof o.rating === 'number')
            .sort((a, b) => new Date(b.orderDate).getTime() - new Date(a.orderDate).getTime());
    }, [orders]);

    return (
        <div className="space-y-6">
            {reviews.map(order => {
                const item = menuItems.find(i => i.id === order.itemId);
                const user = users.find(u => u.id === order.userId);
                return (
                    <div key={order.id} className="bg-white dark:bg-slate-800 p-6 rounded-xl shadow-lg">
                        <div className="flex items-start gap-4">
                            <img src={item?.imageUrl} alt={item?.name} className="w-20 h-20 object-cover rounded-lg flex-shrink-0" />
                            <div className="flex-grow">
                                <div className="flex justify-between items-start">
                                    <div>
                                        <p className="font-bold text-lg text-slate-800 dark:text-slate-100">{item?.name || 'Unknown Item'}</p>
                                        <p className="text-sm text-slate-500 dark:text-slate-400">Reviewed by an employee from <strong>{user?.department || '...'}</strong></p>
                                    </div>
                                    <div className="flex-shrink-0">
                                        <StarRatingDisplay rating={order.rating!} />
                                    </div>
                                </div>
                                {order.review && (
                                    <p className="mt-3 text-slate-700 dark:text-slate-200 bg-slate-50 dark:bg-slate-700/50 p-4 rounded-lg border border-slate-200 dark:border-slate-700">"{order.review}"</p>
                                )}
                            </div>
                        </div>
                    </div>
                );
            })}
             {reviews.length === 0 && (
                <div className="text-center py-16 text-slate-500 dark:text-slate-400 bg-slate-100/50 dark:bg-slate-800/50 rounded-xl">
                    <p className="font-semibold text-lg text-slate-700 dark:text-slate-200">No Reviews Yet</p>
                    <p className="text-sm mt-1">When customers rate their orders, the reviews will appear here.</p>
                </div>
             )}
        </div>
    );
};

const VendorView = () => {
    const { currentUser, orders, updateOrderStatus, menuItems, addMenuItem, deleteMenuItem, showNotification, setViewInvoice } = useAppContext();
    const [filterSlot, setFilterSlot] = useState<string>('all');
    const [startDate, setStartDate] = useState<string>('');
    const [endDate, setEndDate] = useState<string>('');
    const [activeView, setActiveView] = useState<View>('orders');
    const [isAddModalOpen, setIsAddModalOpen] = useState(false);
    const [deletingItem, setDeletingItem] = useState<MenuItem | null>(null);
    
    // New Order Notification State
    const [showNewOrderAlert, setShowNewOrderAlert] = useState(false);
    const prevOrderCountRef = useRef(orders.length);
    const audioRef = useRef<HTMLAudioElement>(null);
    const NOTIFICATION_SOUND = 'data:audio/mpeg;base64,';

    // Effect for new order notification
    useEffect(() => {
        // Only trigger notification if the order count increases
        if (orders.length > prevOrderCountRef.current) {
            setShowNewOrderAlert(true);
            audioRef.current?.play().catch(e => console.error("Audio play failed. User interaction might be required.", e));

            const timer = setTimeout(() => {
                setShowNewOrderAlert(false);
            }, 6000); // Auto-dismiss after 6 seconds

            return () => clearTimeout(timer);
        }
        
        // Always update the ref to the latest count for the next render
        prevOrderCountRef.current = orders.length;
    }, [orders]);

    const pickupSlots = useMemo(() => {
        const slots = new Set(orders.map(o => o.pickupSlot));
        return ['all', ...Array.from(slots).sort()];
    }, [orders]);

    const filteredOrders = useMemo(() => {
        return orders
            .filter(o => filterSlot === 'all' || o.pickupSlot === filterSlot)
            .filter(o => {
                if (!startDate && !endDate) return true;
                const orderDateStr = o.orderDate.substring(0, 10);
                if (startDate && orderDateStr < startDate) {
                    return false;
                }
                if (endDate && orderDateStr > endDate) {
                    return false;
                }
                return true;
            })
            .sort((a, b) => new Date(b.orderDate).getTime() - new Date(a.orderDate).getTime());
    }, [orders, filterSlot, startDate, endDate]);

    const handleStatusChange = async (orderId: string, newStatus: OrderStatus) => {
        await updateOrderStatus(orderId, newStatus);
        showNotification(`Order status updated to ${newStatus}.`);
    };

    const handleConfirmDelete = async () => {
        if (deletingItem) {
            await deleteMenuItem(deletingItem.id);
            setDeletingItem(null);
            showNotification('Menu item has been deleted.');
        }
    };
    
    const handleAddNewItem = async (newItem: Omit<MenuItem, 'id' | 'vendorId'>) => {
        await addMenuItem(newItem);
        showNotification('New menu item added!');
    };

    const handleClearFilters = () => {
        setFilterSlot('all');
        setStartDate('');
        setEndDate('');
    };
    
    const areFiltersActive = filterSlot !== 'all' || startDate !== '' || endDate !== '';
    
    if (currentUser?.role !== 'VENDOR') return null;

    return (
        <div className="container mx-auto px-4 py-8">
            <audio ref={audioRef} src={NOTIFICATION_SOUND} preload="auto"></audio>
            <h2 className="text-4xl font-extrabold text-slate-800 dark:text-slate-100 mb-2">Vendor Dashboard</h2>
            <div className="flex items-center gap-4">
              <p className="text-lg text-slate-600 dark:text-slate-300">Welcome, {currentUser?.name}.</p>
              <div className="flex items-center gap-1.5 bg-yellow-100 text-yellow-800 dark:bg-yellow-400/20 dark:text-yellow-300 font-bold px-3 py-1 rounded-full">
                <StarIcon className="w-4 h-4" />
                <span>{currentUser.averageRating.toFixed(1)}</span>
              </div>
            </div>


            {/* View Tabs */}
            <div className="mt-8 mb-8 border-b border-slate-200 dark:border-slate-700">
                <nav className="-mb-px flex space-x-8">
                <button onClick={() => setActiveView('orders')} className={`py-4 px-1 border-b-2 font-semibold text-base flex items-center gap-2.5 transition-colors ${activeView === 'orders' ? 'border-indigo-500 text-indigo-600 dark:text-indigo-400' : 'border-transparent text-slate-500 dark:text-slate-400 hover:text-slate-700 dark:hover:text-slate-200 hover:border-slate-300 dark:hover:border-slate-500'}`}>
                    <ClockIcon className="w-5 h-5" />
                    Incoming Orders
                </button>
                <button onClick={() => setActiveView('menu')} className={`py-4 px-1 border-b-2 font-semibold text-base flex items-center gap-2.5 transition-colors ${activeView === 'menu' ? 'border-indigo-500 text-indigo-600 dark:text-indigo-400' : 'border-transparent text-slate-500 dark:text-slate-400 hover:text-slate-700 dark:hover:text-slate-200 hover:border-slate-300 dark:hover:border-slate-500'}`}>
                    <MenuIcon className="w-5 h-5" />
                    Manage Menu
                </button>
                 <button onClick={() => setActiveView('reviews')} className={`py-4 px-1 border-b-2 font-semibold text-base flex items-center gap-2.5 transition-colors ${activeView === 'reviews' ? 'border-indigo-500 text-indigo-600 dark:text-indigo-400' : 'border-transparent text-slate-500 dark:text-slate-400 hover:text-slate-700 dark:hover:text-slate-200 hover:border-slate-300 dark:hover:border-slate-500'}`}>
                    <ClipboardDocumentListIcon className="w-5 h-5" />
                    Reviews
                </button>
                </nav>
            </div>

            {activeView === 'orders' && (
                <>
                    {showNewOrderAlert && (
                         <div className="mb-6 bg-indigo-50 dark:bg-indigo-900/30 border-l-4 border-indigo-500 text-indigo-800 dark:text-indigo-200 p-4 rounded-md flex items-center justify-between shadow-lg" role="alert">
                            <div className="flex items-center">
                                <BellIcon className="h-6 w-6 mr-3 text-indigo-600 animate-bounce"/>
                                <p className="font-bold">A new order has been placed!</p>
                            </div>
                            <button onClick={() => setShowNewOrderAlert(false)} className="p-1.5 text-indigo-500 hover:bg-indigo-200 dark:hover:bg-indigo-500/30 rounded-full" aria-label="Dismiss">
                                <XMarkIcon className="h-5 w-5"/>
                            </button>
                        </div>
                    )}
                    <div className="mb-6 flex flex-col lg:flex-row lg:items-center gap-4 flex-wrap bg-slate-100 dark:bg-slate-800/50 p-4 rounded-lg border dark:border-slate-700">
                        <div className="flex items-center gap-2">
                            <label htmlFor="slotFilter" className="font-semibold text-sm text-slate-700 dark:text-slate-200 flex-shrink-0">Pickup Slot:</label>
                            <div className="relative">
                                <select
                                    id="slotFilter"
                                    value={filterSlot}
                                    onChange={(e) => setFilterSlot(e.target.value)}
                                    className="appearance-none bg-white dark:bg-slate-800 border border-slate-300 dark:border-slate-600 rounded-lg py-1.5 pl-3 pr-8 text-sm text-slate-700 dark:text-slate-200 focus:outline-none focus:ring-1 focus:ring-indigo-500"
                                >
                                    {pickupSlots.map(slot => (
                                        <option key={slot} value={slot}>{slot === 'all' ? 'All Slots' : slot}</option>
                                    ))}
                                </select>
                                <ChevronDownIcon className="w-4 h-4 text-slate-400 absolute top-1/2 right-2.5 transform -translate-y-1/2 pointer-events-none" />
                            </div>
                        </div>

                        <div className="flex items-center gap-4 flex-wrap">
                            <span className="font-semibold text-sm text-slate-700 dark:text-slate-200">Date Range:</span>
                            <div className="flex items-center gap-2">
                                <label htmlFor="startDate" className="text-sm text-slate-600 dark:text-slate-300 flex-shrink-0 sr-only">From</label>
                                <input
                                    type="date"
                                    id="startDate"
                                    value={startDate}
                                    onChange={(e) => setStartDate(e.target.value)}
                                    className="bg-white dark:bg-slate-800 border border-slate-300 dark:border-slate-600 rounded-lg py-1.5 px-2 text-sm text-slate-700 dark:text-slate-200 focus:outline-none focus:ring-1 focus:ring-indigo-500"
                                />
                            </div>
                            <span className="text-slate-500 dark:text-slate-400">-</span>
                            <div className="flex items-center gap-2">
                                <label htmlFor="endDate" className="text-sm text-slate-600 dark:text-slate-300 flex-shrink-0 sr-only">To</label>
                                <input
                                    type="date"
                                    id="endDate"
                                    value={endDate}
                                    onChange={(e) => setEndDate(e.target.value)}
                                    className="bg-white dark:bg-slate-800 border border-slate-300 dark:border-slate-600 rounded-lg py-1.5 px-2 text-sm text-slate-700 dark:text-slate-200 focus:outline-none focus:ring-1 focus:ring-indigo-500"
                                />
                            </div>
                        </div>

                        <div className="flex-grow"></div>
                        
                        {areFiltersActive && (
                            <button 
                                onClick={handleClearFilters}
                                className="flex items-center justify-center gap-1.5 text-sm font-semibold text-indigo-600 dark:text-indigo-400 hover:text-indigo-800 dark:hover:text-indigo-300 transition-colors px-3 py-1.5 rounded-lg hover:bg-indigo-100 dark:hover:bg-indigo-900/50 lg:ml-auto"
                                aria-label="Clear filters"
                            >
                                <XMarkIcon className="w-4 h-4" />
                                <span>Clear</span>
                            </button>
                        )}
                    </div>

                    <div className="bg-white dark:bg-slate-800 rounded-xl shadow-lg overflow-x-auto">
                        <table className="w-full text-sm text-left text-slate-500 dark:text-slate-400">
                            <thead className="text-xs text-slate-700 dark:text-slate-300 uppercase bg-slate-50 dark:bg-slate-700/50">
                                <tr>
                                    <th scope="col" className="px-6 py-4 font-semibold">Employee</th>
                                    <th scope="col" className="px-6 py-4 font-semibold">Item</th>
                                    <th scope="col" className="px-6 py-4 font-semibold">Payment</th>
                                    <th scope="col" className="px-6 py-4 font-semibold">Status</th>
                                    <th scope="col" className="px-6 py-4 font-semibold">Update Status</th>
                                </tr>
                            </thead>
                            <tbody>
                                {filteredOrders.map(order => {
                                    const user = MOCK_USERS.find(u => u.id === order.userId);
                                    const item = menuItems.find(i => i.id === order.itemId);
                                    return (
                                        <tr 
                                          key={order.id} 
                                          className="bg-white dark:bg-slate-800 border-b dark:border-slate-700 hover:bg-slate-50/50 dark:hover:bg-slate-700/50 cursor-pointer transition-colors"
                                          onClick={() => setViewInvoice(order)}
                                        >
                                            <td className="px-6 py-4">
                                                <div className="font-bold text-base text-slate-800 dark:text-slate-100">{user?.name || 'Unknown User'}</div>
                                                <div className="flex items-center gap-1.5 text-xs text-slate-500 dark:text-slate-400 mt-1">
                                                    <BuildingOfficeIcon className="w-4 h-4 text-slate-400 flex-shrink-0" />
                                                    <span>{user?.department || 'Unknown Dept.'}</span>
                                                </div>
                                            </td>
                                            <td className="px-6 py-4">
                                                <div className="flex items-center gap-3">
                                                    <img src={item?.imageUrl} alt={item?.name || ''} className="w-14 h-14 object-cover rounded-lg"/>
                                                    <span className="font-semibold text-slate-800 dark:text-slate-100">{item?.name || 'Unknown Item'}</span>
                                                </div>
                                            </td>
                                            <td className="px-6 py-4">
                                                <div className="flex items-center gap-2">
                                                    {order.paymentStatus === 'Paid' ? <ShieldCheckIcon className="w-5 h-5 text-green-500" /> : <CashIcon className="w-5 h-5 text-slate-500 dark:text-slate-400" />}
                                                    <div>
                                                        <div className="font-semibold text-slate-700 dark:text-slate-200">{order.paymentMethod}</div>
                                                        <div className={`text-xs font-bold ${order.paymentStatus === 'Paid' ? 'text-green-600 dark:text-green-400' : 'text-slate-500 dark:text-slate-400'}`}>{order.paymentStatus}</div>
                                                    </div>
                                                </div>
                                            </td>
                                            <td className="px-6 py-4"><OrderStatusBadge status={order.status} /></td>
                                            <td className="px-6 py-4">
                                                <div className="relative" onClick={e => e.stopPropagation()}>
                                                    <select
                                                        value={order.status}
                                                        onChange={(e) => handleStatusChange(order.id, e.target.value as OrderStatus)}
                                                        className="appearance-none bg-white dark:bg-slate-800 border border-slate-300 dark:border-slate-600 rounded-lg py-2 pl-3 pr-8 text-slate-700 dark:text-slate-200 focus:outline-none focus:ring-1 focus:ring-indigo-500"
                                                        disabled={order.status === OrderStatus.PickedUp}
                                                    >
                                                        {Object.values(OrderStatus).map(status => (
                                                            <option key={status} value={status}>{status}</option>
                                                        ))}
                                                    </select>
                                                    <ChevronDownIcon className="w-4 h-4 text-slate-400 absolute top-1/2 right-2.5 transform -translate-y-1/2 pointer-events-none" />
                                                </div>
                                            </td>
                                        </tr>
                                    );
                                })}
                            </tbody>
                        </table>
                        {filteredOrders.length === 0 && <div className="text-center py-16 text-slate-500 dark:text-slate-400"><p className="font-semibold text-lg text-slate-700 dark:text-slate-200">No orders found</p><p className="mt-1">There are no orders matching the current filter.</p></div>}
                    </div>
                </>
            )}

            {activeView === 'menu' && (
                <ManageMenuView 
                    menuItems={menuItems} 
                    onAddNew={() => setIsAddModalOpen(true)}
                    onDelete={(item) => setDeletingItem(item)}
                />
            )}
            
            {activeView === 'reviews' && (
                <ReviewsView orders={orders} menuItems={menuItems} users={MOCK_USERS} />
            )}

            {isAddModalOpen && (
                <AddMenuItemModal 
                    onClose={() => setIsAddModalOpen(false)} 
                    onAdd={handleAddNewItem} 
                />
            )}

            {deletingItem && (
                <DeleteConfirmationModal
                    item={deletingItem}
                    onClose={() => setDeletingItem(null)}
                    onConfirm={handleConfirmDelete}
                />
            )}
        </div>
    );
};

export default VendorView;